import Foundation

protocol SynchsafeEncoder {
    func encode(integer: UInt32) -> UInt32
}

//
//  SynchsafeIntegerDecoder.swift
//
//  Created by Fabrizio Duroni on 21/12/2018.
//  2018 Fabrizio Duroni.
//

import Foundation

/**
 Synchsafe integer decoder

 - SeeAlso https://handwiki.org/wiki/Synchsafe
 */
class SynchsafeIntegerDecoder {
    func decode(integer: UInt32) -> UInt32 {
        var decodedInteger: UInt32 = 0
        var mask: UInt32 = 0x7F000000

        while mask != 0 {
            decodedInteger = decodedInteger >> 1
            decodedInteger = decodedInteger | integer & mask
            mask >>= 8
        }

        return decodedInteger
    }
}

//
//  SynchsafeIntegerEncoder.swift
//
//  Created by Fabrizio Duroni on 22/12/2018.
//  2018 Fabrizio Duroni.
//

import Foundation

/**
 Synchsafe integer encoder

 - SeeAlso https://handwiki.org/wiki/Synchsafe
 */
class SynchsafeIntegerEncoder: SynchsafeEncoder {
    func encode(integer: UInt32) -> UInt32 {
        var encodedInteger: UInt32 = 0
        var mask: UInt32 = 0x7F
        var partiallyEncodedInteger = integer
        while mask != 0x7FFFFFFF {
            encodedInteger = partiallyEncodedInteger & ~mask
            encodedInteger = encodedInteger << 1
            encodedInteger = encodedInteger | partiallyEncodedInteger & mask
            mask = ((mask + 1) << 8) - 1
            partiallyEncodedInteger = encodedInteger
        }
        return encodedInteger
    }
}

//
//  UInt32ToByteArrayAdapter.swift
//
//  Created by Fabrizio Duroni on 19/02/2018.
//  2018 Fabrizio Duroni.
//

import Foundation

protocol UInt32ToByteArrayAdapter {
    func adapt(uInt32: UInt32) -> [UInt8]
}

//
//  UInt32ToByteArrayAdapterUsingUnsafePointer.swift
//
//  Created by Fabrizio Duroni on 19/02/2018.
//  2018 Fabrizio Duroni.
//

import Foundation

class UInt32ToByteArrayAdapterUsingUnsafePointer: UInt32ToByteArrayAdapter {
    func adapt(uInt32: UInt32) -> [UInt8] {
        var currentUInt32 = uInt32
        let bytes = withUnsafePointer(to: &currentUInt32) {
            $0.withMemoryRebound(to: UInt8.self, capacity: MemoryLayout<UInt32>.size) {
                Array(UnsafeBufferPointer(start: $0, count: MemoryLayout<UInt32>.size))
            }
        }
        return bytes.reversed()
    }
}

