//
//  ID3GenreFrameCreator.swift
//
//  Created by Fabrizio Duroni on 04/03/2018.
//  2018 Fabrizio Duroni.
//

import Foundation

class ID3GenreFrameCreator: ID3FrameCreator {
    private let frameCreator: FrameFromStringContentCreator

    init(frameCreator: FrameFromStringContentCreator) {
        self.frameCreator = frameCreator
    }

    func createFrames(id3Tag: ID3Tag) -> [UInt8] {
        if let genreFrame = id3Tag.frames[.genre] as? ID3FrameGenre {
           return frameCreator.createFrame(
                    frameType: .genre,
                    version: id3Tag.properties.version,
                    content: adapt(genre: genreFrame)
            )
        }
        return []
    }

    private func adapt(genre: ID3FrameGenre) -> String {
        var genreString = ""
        if let genreIdentifier = genre.identifier {
            genreString = "(\(genreIdentifier.rawValue))"
        }
        genreString += "\(genre.description ?? "")"
        return genreString
    }
}

//
//  ID3ISO88591StringToByteAdapter.swift
//
//  Created by Fabrizio Duroni on 27/04/2018.
//  2018 Fabrizio Duroni.
//

import Foundation

class ID3ISO88591StringToByteAdapter: StringToBytesAdapter {
    private let paddingAdder: PaddingAdder
    private let frameConfiguration: ID3FrameConfiguration

    init(paddingAdder: PaddingAdder, frameConfiguration: ID3FrameConfiguration) {
        self.paddingAdder = paddingAdder
        self.frameConfiguration = frameConfiguration
    }

    func adapt(string: String, for version: ID3Version) -> [UInt8] {
        return frameConfiguration.encodingByteFor(version: version, encoding: .ISO88591) +
            paddingAdder.addTo(content: [UInt8](string.utf8), numberOfByte: 1)
    }
}

//
//  ID3LocalizedFrameCreator.swift
//  ID3TagEditor
//
//  Created by Fabrizio Duroni on 14/10/20.
//  2020 Fabrizio Duroni.
//

import Foundation

class ID3LocalizedFrameCreator: LocalizedFrameCreator {
    private let id3FrameConfiguration: ID3FrameConfiguration
    private let frameHeaderCreator: FrameHeaderCreator
    private let paddingAdder: PaddingAdder

    init(id3FrameConfiguration: ID3FrameConfiguration,
         frameHeaderCreator: FrameHeaderCreator,
         paddingAdder: PaddingAdder
    ) {
        self.id3FrameConfiguration = id3FrameConfiguration
        self.frameHeaderCreator = frameHeaderCreator
        self.paddingAdder = paddingAdder
    }

    func createFrame(using frameContent: ID3FrameWithLocalizedContent,
                     version: ID3Version,
                     frameType: FrameType) -> [UInt8] {
        let frameBody = createFrameBodyUsing(frameContent: frameContent, version: version)
        let frameHeader = frameHeaderCreator.createUsing(
            version: version,
            frameType: frameType,
            frameBody: frameBody
        )
        return frameHeader + frameBody
    }

private func createFrameBodyUsing(frameContent: ID3FrameWithLocalizedContent,
                                      version: ID3Version) -> [UInt8] {
        return id3FrameConfiguration.encodingByteFor(version: version, encoding: .UTF16)
            + [UInt8](frameContent.language.rawValue.data(using: .utf8)!)
            + createFrameTextContentFrom(frameLocalized: frameContent)
    }

    private func createFrameTextContentFrom(frameLocalized: ID3FrameWithLocalizedContent) -> [UInt8] {
        let contentDescriptor = paddingAdder.addTo(
            content: [UInt8](frameLocalized.contentDescription.data(using: .utf16)!),
            numberOfByte: 2
        )
        let content = frameLocalized.content.data(using: .utf16)!
        return contentDescriptor + content
    }
}

