    /// The version of the tag (see `ID3Version`).
    public var version: ID3Version

    init(version: ID3Version) {
        self.version = version
        self.size = 0
    }

    init(version: ID3Version, size: UInt32) {
        self.version = version
        self.size = size
    }
}

